package com.yeying.aimi.protoco;public enum CacheClearType {    SMS_PROVIDER("SmsProvider", "/client/smsprovider", 1);    private final String clearApiName;    private final String clearApiUrl;    private final int clearApiType;    private CacheClearType(String name, String url, int type) {        this.clearApiName = name;        this.clearApiUrl = url;        this.clearApiType = type;    }    int getClearType() {        return clearApiType;    }    String getClearName() {        return clearApiName;    }    String getClearUrl() {        return clearApiUrl;    }}